import React, { useState } from 'react';
import styles from './LoginPage.module.css';
import api from '../../api';
import { Navigate, useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const LoginPage = () => {
  const [userData, setUserData] = useState({
    email: ' ',
    password: ' ',
  });

  const navigate = useNavigate();

  const handleUserLogIn = async (e) => {
    e.preventDefault();
    localStorage.removeItem('user_token');
    const response = await api.post('/authorize', userData);
    const { token } = response.data;
    localStorage.setItem('user_token', token);
    console.log(response.data);
    navigate('/home');
  };

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  return (
    <form className={styles.formContainer} onSubmit={handleUserLogIn}>
      {/* <div>
        <h1 className={styles.title}>Authorizing Page</h1>
        <label className={styles.label}>Email: </label>
        <input
          type="email"
          name="email"
          value={userData.email}
          onChange={handleChange}
          className={styles.inputField}
        ></input>
        <label className={styles.label}>Password: </label>
        <input
          type="password"
          name="password"
          value={userData.password}
          onChange={handleChange}
          className={styles.inputField}
        ></input>
        <Link to='/register'>Create a new account</Link>
        <button type="submit" className={styles.submitButton}>
          Log in
        </button>
      </div> */}


      

<div class="login">
  <div class="form">
    <h2>Hello User</h2>
    <div class="form-field">
      <label for="login-mail"><i class="fa fa-user"></i></label>
      <input id="login-mail" type="text" name="mail" placeholder="E-Mail" pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$" required/>
      <svg>
        <use href="#svg-check" />
      </svg>
    </div>
    <div class="form-field">
      <label for="login-password"><i class="fa fa-lock"></i></label>
      <input id="login-password" type="password" name="password" placeholder="Password" pattern=".{6,}" required/>
      <svg>
        <use href="#svg-check" />
      </svg>
    </div>
    <button type="submit" class="button">
      <div class="arrow-wrapper">
        <span class="arrow"></span>
      </div>
      <p class="button-text">SIGN IN</p>
    </button>
  </div>
  <div class="finished">
    <svg>
      <use href="#svg-check" />
    </svg>
  </div>
</div>



<svg style="display:none;">
  <symbol id="svg-check" viewBox="0 0 130.2 130.2">
    <polyline points="100.2,40.2 51.5,88.8 29.8,67.5 "/>
  </symbol>
</svg>



    </form>
  );
};

export default LoginPage;
